  // محاسبات پیچیده روی داده‌ها
  const processedData = useMemo(() => {
    return data.map((item) => ({
      ...item,
      processed: expensiveOperation(item),
    }));
  }, [data]);

  // ایجاد تابع callback
  const handleSelect = useCallback(
    (item) => {
      console.log("آیتم انتخاب شد:", item);
      onItemSelect(item);
